import React, { useState, useEffect } from 'react';
import './movies.css'



function ApiTest (){

    const [ isLoading, setIsLoading ] = useState(true);
    const [ movies, setMovies ] = useState([]);
    
  const baseURL = 'https://api.themoviedb.org/3/movie/';
  const apiKey = 'fb6347fd6c56bdb6a17dca07b0c83079';
  
    
    //component did mount

    useEffect(() => {
        const getData = async () =>{
            let endPoint =  `${baseURL}popular?api_key=${apiKey}&language=en-US&page=1`; 
            let response = await fetch(endPoint);
            let data = await response.json();
            return data;  
           } 
            getData().then(data =>{
               setMovies(data.results);
               console.log(data.results);
           });
           
    },[])
    

    return(
        <div className= "movies">
            {movies.map(oneMovie =>{
                let imagenMovie = `https://image.tmdb.org/t/p/w500${oneMovie.backdrop_path}`;
                return(
                    <div key={oneMovie.id}>
                        
                        <img src = {imagenMovie} alt="" />
                        <h3> {oneMovie.title} </h3>
                        <p> {oneMovie.overview} </p>

                    </div>
                )
            } )}
          
          
       </div>
         
     )
 }
 export default ApiTest;
