import { useState } from "react";
import { Navbar , Nav, Form, FormControl, Button } from 'react-bootstrap';
import { Link, useHistory } from 'react-router-dom';
import './navbar.css'

function NavbarComp (){
    
  let history = useHistory();
  let [ alert , setAlert ]= useState(false)

  let submitHandler = (e) => {
    e.preventDefault();
    let word =e.target.search.value;
    if(word.length>=3){
      history.push(`/search-results?search=${word}`)
      setAlert(false)
      e.currentTarget.reset()
      }else{
        setAlert(true)
      }
    
  }
  
  
  
  
  return(
        
      <Navbar bg="dark" variant="dark"  expand="lg">
      <Navbar.Brand >MovieFlix</Navbar.Brand>
      <Navbar.Toggle aria-controls="navbarScroll" />
      <Navbar.Collapse id="navbarScroll">
        <Nav
          className="mr-auto my-2 my-lg-0"
          style={{ maxHeight: '100px' }}
          navbarScroll
        >
          <Nav.Link className="buttonLink" as={Link} to="/">Home</Nav.Link>
          <Nav.Link className="buttonLink" as={Link} to="/favoritos">Favoritos ‚ù§</Nav.Link>
          
         
        </Nav>
        <Form className="d-flex" onSubmit={submitHandler}>
          <FormControl
             type="text"
             placeholder="Search"
             className="mr-2"
             aria-label="Search"
             name="search"
          />
          <Button className="searchButton" type="submit">Search</Button>

        </Form>
      </Navbar.Collapse>
    </Navbar>
            
       
        
    );
}

export default NavbarComp;
