import React, { useState, useEffect } from 'react';
import { Card, Button, Row, Container, Col } from 'react-bootstrap';
import './movies.css'



function Movies (){

    const [ isLoading, setIsLoading ] = useState(true);
    const [ movies, setMovies ] = useState([]);
    
  const baseURL = 'https://api.themoviedb.org/3/movie/';
  const apiKey = 'fb6347fd6c56bdb6a17dca07b0c83079';
  
    
    //component did mount

    useEffect(() => {
        const getData = async () =>{
            let endPoint =  `${baseURL}popular?api_key=${apiKey}&language=en-US&page=1`; 
            let response = await fetch(endPoint);
            let data = await response.json();
            return data;  
           } 
            getData().then(data =>{
               setMovies(data.results);
               setIsLoading(false);
           });
           
    },[])
    

    return(
        <div className= "movies">
            
             
              
                  {isLoading && <h3> Loading...</h3> }
               {movies.map(oneMovie =>{
                let imagenMovie = `https://image.tmdb.org/t/p/w500${oneMovie.backdrop_path}`;
                return(
                    <div key={oneMovie.id}>
                        <Container>
                        <Row>
                        <Col lg={3} sm={6} xs ={12}>
                      <Card style={{ width: '18rem' }}>
                       <Card.Img variant="top" src = {imagenMovie} />
                       <Card.Body>
                        <Card.Title>{oneMovie.title}</Card.Title>
                        <Card.Text>{oneMovie.overview}</Card.Text>
                       </Card.Body>
                       <Button className="favoritos">Add to Favorites</Button>
                      </Card>
                      </Col>
                      </Row>
                      </Container>
                    </div>
                )
            } )}
            
           
          
       </div>
         
     )
 }
 export default Movies;
